import { Component } from '@angular/core';
import { NgForm } from '@angular/forms';
import { NgIf } from '@angular/common';
import { NgModel } from '@angular/forms';
@Component({
  selector: 'app-ajout',
  templateUrl: './ajout.component.html',
  styleUrls: ['./ajout.component.css']

})
export class AjoutComponent {
  utilisateur = {
    nom: '',
    prenom: '',
    email: '',
    phoneNumber: '',
    age:'',
    cin: '',
    maladies: [],
    tension: ''
 };
 isInvalid: boolean = false; // Variable pour vérifier la validation au focus

  onFocus() {
    this.isInvalid = true; // Activer la validation au focus
  }

  onBlur() {
    this.isInvalid = false; // Désactiver la validation au blur
  }
 maladies = [
    {id: 1, nom: 'hypertension artérielle'},
    {id: 2, nom: 'Insuffisance cardiaque'},


 ];
 onCheckChange(event: any, maladieId: number) {
  if (event.target.checked) {
      this.utilisateur.maladies.push();
  } else {
      this.utilisateur.maladies = this.utilisateur.maladies.filter(maladie => maladie !== maladieId);
  }
}
 ajouterUtilisateur() {
    console.log(this.utilisateur);
 }

 maladiesList = ['Hypertension', 'Insuffisance cardiaque'];

 onSubmit(utilisateurForm: NgForm) {
   console.log(utilisateurForm)
 }
 message: string = ''; 

 displayMessage(): void {
   this.message = 'Votre demande est ajoutée';
 }
 showCard: boolean = false;


 toggleCard() {
   this.showCard = !this.showCard;
 }

 imageSrc: string | ArrayBuffer | null = null;

  constructor() { }

  onFileSelected(event: any): void {
    const file: File = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e: any) => {
        this.imageSrc = e.target.result;
      };
      reader.readAsDataURL(file);
    }
  }

}
<table class="table">
  <thead>
    <tr>
      <th colspan="container">
        <div class="h2">
          <a routerLink="/index"><img class="aj" src="assets/images/retour.png"> </a>  AJOUTER UTILISATEUR 
        </div>
      </th>   
    </tr>

    <th>
      <div class="container">
          <img class="b" src="assets/images/garcon.png" ><br><br><br><br><br><br><br>

          <div>
            <input type="file" (change)="onFileSelected($event)" style="display: none;" #fileInput>
            <button (click)="fileInput.click()">Importer une image</button>
          </div>

          <div *ngIf="imageSrc">
            <img [src]="imageSrc" alt="Image sélectionnée" width="200" height="200">
          </div>

                <form #utilisateurForm="ngForm" (ngSubmit)="onSubmit(utilisateurForm)">
             <div class="form-group">

               <input type="text" class="nom" [style.width.px]="350" class="form-control" id="nom" placeholder="Nom" [(ngModel)]="utilisateur.nom" [ngModelOptions]="{ standalone: true }" #nom="ngModel" 
               ngModel required >
               <div  *ngIf="nom.errors?.['required'] && nom.touched">
                il est nécessaire de remplir ce champs!!!!!
              </div>
              </div><br>
             <div class="form-group">
               <input type="text" [style.width.px]="350" class="form-control" placeholder="Prénom" id="prenom" [(ngModel)]="utilisateur.prenom" [ngModelOptions]="{ standalone: true }" #prenom="ngModel" 
               ngModel required >
               <div  *ngIf="prenom.errors?.['required'] && prenom.touched">
                il est nécessaire de remplir ce champs!!!!!
              </div>
              </div><br>

             <div class="form-group">
              <input type="text" [style.width.px]="350" class="form-control" placeholder="Email" id="email" [(ngModel)]="utilisateur.email" [ngModelOptions]="{ standalone: true }" #email="ngModel" 
              ngModel required   >
              <div  *ngIf="email.errors?.['required'] && email.touched">
                il est nécessaire de remplir ce champs!!!!!
              </div>
            </div><br>

            <div class="form-group" >
              <input type="text" [style.width.px]="350" class="form-control" placeholder="Numéro de téléphone" id="phoneNumber" [(ngModel)]="utilisateur.phoneNumber" [ngModelOptions]="{ standalone: true }" #phoneNumber="ngModel" 
              ngModel required>
              <div *ngIf="phoneNumber.errors?.['required'] && phoneNumber.touched">
                il est nécessaire de remplir ce champs!!!!!
              </div>
